package br.com.bradescoseguros.opin.external.configuration.swagger;

import org.springframework.beans.factory.annotation.Value;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import springfox.documentation.builders.ApiInfoBuilder;
import springfox.documentation.builders.PathSelectors;
import springfox.documentation.builders.RequestHandlerSelectors;
import springfox.documentation.service.ApiInfo;
import springfox.documentation.service.Contact;
import springfox.documentation.spi.DocumentationType;
import springfox.documentation.spring.web.plugins.Docket;
import springfox.documentation.swagger2.annotations.EnableSwagger2;

@Configuration
@EnableSwagger2
public class SwaggerConfig {

    private static final String BASE_PACKAGE = "br.com.bradescoseguros.opin.interfaceadapter.autogenerated.delegate";

    @Value("${info.application.swaggerVersion}")
    private String swagerVersion;

    @Bean
    public Docket device() {
        return new Docket(DocumentationType.SWAGGER_2)
                .apiInfo(apiInfo())
                .forCodeGeneration(false)
                .select()
                .apis(requestHandler -> RequestHandlerSelectors.basePackage(BASE_PACKAGE).test(requestHandler))
                .paths(PathSelectors.any())
                .build();
    }

    ApiInfo apiInfo() {
        return new ApiInfoBuilder()
                .title("API Fornecimento de Produtos Residenciais")
                .description("Fornecimento de Produtos Residenciais.")
                .license("")
                .licenseUrl("http://unlicense.org")
                .termsOfServiceUrl("")
                .version(swagerVersion)
                .contact(new Contact("", "", ""))
                .build();
    }

}